mapper.insert:
INSERT INTO board (
	title
	, content
	, latitude
	, longitude
	, view_cnt
	, reg_id
	, reg_dtt
	, use_yn
)
VALUES (
	#{title}
	, #{content}
	, #{latitude}
	, #{longitude}
	, #{viewCnt}
	, #{regId}
	, now()
	, 'Y'
)

mapper.update:
UPDATE board
SET title = #{title}
	, content = #{content}
	, latitude = #{latitude}
	, longitude = #{longitude}
WHERE board_id = #{boardId}

UPDATE board
SET use_yn = 'N'
WHERE board_id = #{boardId}
