mapper.select:
SELECT id
	, name
FROM user
WHERE use_yn = 'Y'
	AND id = #{id}
	AND pw = #{pw}

mapper.insert:
INSERT INTO user (
	id
	, pw
	, name
	, use_yn
)
VALUES (
	#{id}
	, #{pw}
	, #{name}
	, 'Y'
)

mapper.update:
UPDATE user
SET pw = #{pw}
WHERE id = #{id}

UPDATE user
SET use_yn = 'N'
WHERE id = #{id}
